import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'angular-widget-project';

  data: any[] = [];
  pieChartData: any[] = [];

  constructor(private http: HttpClient) { }

  ngOnInit(): void {
    this.http.get('https://randomapi.com/api/6de6abfedb24f889e0b5f675edc50deb?fmt=raw&sole')
      .subscribe((response: any) => {
        this.data = response;
        this.pieDataSort()
        console.log("data ", this.data)
      });



  }

  pieDataSort() {
    const newArray = this.data
      .slice(0, 10) // Take the first 20 records
      .map(item => {
        const label = `${item.first} ${item.last}`;
        const balance = parseFloat(item.balance.replace(/[$,]/g, ''));
        return { label, value: balance };
      });
    this.pieChartData = newArray;
    console.log("pieChartData ", this.pieChartData);
  }

}
<div class="container data-container">
  <!-- <app-pie-chart [pieChartData]="pieChartData"></app-pie-chart> -->
  <app-pie-chart *ngIf="pieChartData.length > 0" [pieChartData]="pieChartData"></app-pie-chart>

  <app-data-table [data]="data"></app-data-table>
</div>
